import React, { Component } from "react";
import { fetchPosts, fetchLatestPost } from "../actions/postActions";
import { connect } from "react-redux";
import PropTypes from "prop-types";

import ListGroup from "react-bootstrap/ListGroup";

class Posts extends Component {
  componentDidMount() {
    console.log(this.props);
    this.props.fetchPosts();
  }

  componentWillReceiveProps(nextProps) {
    console.log(nextProps);
    const newPost = nextProps.newPost;

    if (
      !(Object.entries(newPost).length === 0 && newPost.constructor === Object)
    ) {
      this.props.fetchLatestPost();
    }
  }

  render() {
    const postItems = this.props.posts.map(post => (
      <ListGroup.Item key={post.id}>
        <h4>{post.title}</h4>
        <br />
        {post.body}
        <br />
        {post.user}
      </ListGroup.Item>
    ));
    return <ListGroup>{postItems}</ListGroup>;
  }
}

Posts.propTypes = {
  fetchPosts: PropTypes.func.isRequired,
  posts: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  posts: state.posts.items,
  newPost: state.posts.item,
  isLogged: state.user.isLogged
});

export default connect(
  mapStateToProps,
  { fetchPosts, fetchLatestPost }
)(Posts);
